import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "./Navbar";
import jwtDecode from "jwt-decode";

export default function Employees() {
  const token = localStorage.getItem("token");
  if (!token) {
    window.location.href = "/login";
  }
  if (!jwtDecode(token).roles.includes("ROLE_ADMIN")) {
    window.location.href = "/visits";
  }

  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [employees, setEmployees] = useState([]);
  const [newEmployee, setNewEmployee] = useState({
    firstname: "",
    lastname: "",
  });

  useEffect(() => {
    const token = localStorage.getItem("token");
    const config = {
      headers: { Authorization: `Bearer ${token}` },
    };
    axios.get("http://127.0.0.1:8000/api/employees", config).then(
      (response) => {
        setIsLoaded(true);
        const employees = response.data["hydra:member"];
        console.log(employees);
        setEmployees(employees);
      },
      (error) => {
        setIsLoaded(true);
        setError(error);
      }
    );
  }, []);

  if (error) {
    return (
      <>
        <Navbar current="employees" />
        <div className="m-10">
          <div>Error: {error.message}</div>
        </div>
      </>
    );
  } else if (!isLoaded) {
    return (
      <>
        <Navbar current="employees" />
        <div className="m-10">
          <div>Loading...</div>
        </div>
      </>
    );
  } else {
    return (
      <>
        <Navbar current="employees" />
        <table>
          <thead>
            <tr>
              <td>Nom</td>
              <td>Pr√©nom</td>
              <td>Suppr.</td>
            </tr>
          </thead>
          <tbody>
            {employees.map((employee) => (
              <tr>
                <td>{employee.lastname}</td>
                <td>{employee.firstname}</td>
                <td>
                    
                      <button onClick={()=>handleDelete(employee.id)}><img src="../assets/delete.png"/></button>
                    
                  </td>           
                </tr>
            ))}
          </tbody>
        </table>

      </>
    );
  }
  function handleSubmit(event) {
    event.preventDefault();
    console.log(event.target);
  const token = localStorage.getItem("token");
    const config = {
      headers: { Authorization: `Bearer ${token}` },
    };
    axios
      .post("http://127.0.0.1:8000/api/employees", newEmployee, config)
      .then((response) => {
        console.log(response);
        window.location.reload();
      })
  }
  function handleChange(event) {
    event.persist();
    setNewEmployee((newEmployee) => ({
      ...newEmployee,
      [event.target.name]: event.target.value,
    }));
  }
  function handleDelete(employeeId) {
  const token = localStorage.getItem("token");
    const config = {
      headers: { Authorization: `Bearer ${token}` },
    };
    axios
      .delete("http://127.0.0.1:8000/api/employees/"+employeeId, config)
      .then((response) => {
        console.log(response);
        window.location.reload();
      })
  }
}
