import React from "react";
import dayjs from "dayjs";
import axios from "axios";

export default function VisitsTableData(props) {
    return(
        <>
                <tbody>
                    <tr key={props.visitors.id}>
                        <td>{props.visitors.firstname}</td>
                        <td>{props.visitors.lastname}</td>
                        <td>{props.visitors.company}</td>
                        {/* <td>{props.visitors.reason.reasonName}</td> */}
                        {/* <td>{props.visitors.encouteredPerson.firstname} {props.visitors.encouteredPerson.lastname}</td> */}
                        <td>{dayjs(props.visitors.entranceDate).format('DD/MM/YY HH:mm')}</td>
                        <td><button onClick={handleClick} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Sortie</button></td>
                    </tr>
                </tbody>            
        </>
    );
    function handleClick(e) {
        e.preventDefault();
        console.log(props.visitors);
        const token = localStorage.getItem("token");
        const config = {
          headers: { Authorization: `Bearer ${token}` },
        };
        const newVisit = {
          leavingDate: dayjs(),
        };
        console.log(newVisit);
        axios
          .put(`http://127.0.0.1:8000/api/visits/${props.visitors.id}`, newVisit)
          .then((response) => {
            console.log(response);
            window.location.reload();
          })
      }
}
