import React, { useState, useEffect } from "react";
import Navbar from "./Navbar";
import axios from "axios";
import jwtDecode from "jwt-decode";


export default function User() {
    const token = localStorage.getItem("token");
    if (!token) {
      window.location.href = "/login";
    }
    if (!jwtDecode(token).roles.includes("ROLE_ADMIN")) {
      window.location.href = "/visits";
    }
    const decodedToken = jwtDecode(token);
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [users, setUsers] = useState([]);
  
  
    useEffect(() => {
        const token = localStorage.getItem('token');
        const config = {
            headers: { Authorization: `Bearer ${token}` }
        };
        axios.get('http://127.0.0.1:8000/api/users', config)
            .then((response) => {
                            setIsLoaded(true);
                            const visitors = response.data['hydra:member'];
                            console.log(visitors);
                            setUsers(visitors);
                        },
                        (error) => {
                            setIsLoaded(true);
                            setError(error);
                        });
    }, [])

    if (error) {
        return(
            <><Navbar current="./users" />
            <div className='m-10'>
                Error: { error.message }
            </div>;
        </>
        );
    } else if (!isLoaded) {
        return(
            <>
                <Navbar current="./users" />
                <div className='m-10'>Loading...</div>
            </>
        );
    } else {
        return (
            <>
                <Navbar current="./users" />
                <form className="w-full text">
                    <h1 className="ml-20 mt-20 font-bold text-2xl pb-8">Modifier nouveau mot de passe :</h1>
                    <div className="sm:col-span-3 w-full ml-40">
                       
                        <label
                        htmlFor="password"
                        className="text-sm font-semibold text-lg leading-6 text-gray-900"
                        >
                        </label>
                        <input className="border w-20" type="password" placeholder="Ancien mot de passe"/>
                    </div>
                </form>
            </>
        )
    }
}
