import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "./Navbar";
import jwtDecode from "jwt-decode";

export default function Employees() {
  const token = localStorage.getItem("token");
  if (!token) {
    window.location.href = "/login";
  }
  if (!jwtDecode(token).roles.includes("ROLE_ADMIN")) {
    window.location.href = "/visits";
  }


  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [reason, setNewReasons] = useState([]);
  const [reasons, setReasons] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const config = {
      headers: { Authorization: `Bearer ${token}` },
    };
    axios.get("http://127.0.0.1:8000/api/reasons", config).then(
      (response) => {
        setIsLoaded(true);
        const reasons = response.data["hydra:member"];
        console.log(reasons);
        setReasons(reasons);
      },
      (error) => {
        setIsLoaded(true);
        setError(error);
      }
    );
  }, []);

  if (error) {
    return (
      <>
        <Navbar current="reasons" />
        <div className="m-10">
          <div>Error: {error.message}</div>
        </div>
      </>
    );
  } else if (!isLoaded) {
    return (
      <>
        <Navbar current="reasons" />
        <div className="m-10">
          <div>Loading...</div>
        </div>
      </>
    );
  } else {
    return (
      <>
        <Navbar current="reasons" />
        <div className="m-10">
          <ul>
            {reasons.map((reason) => (
              <li className="pb-2">
                {reason.reasonName}
              </li>
            ))}
          </ul>
        </div>
        <form className="m-20">
          <h1>Saisie d'un nouveau motif de visite :</h1>
          <div className="sm:col-span-3">
            <label
              htmlFor="reasonName"
              className="text-sm font-medium leading-6 text-gray-900"
            >
              Motif
            </label>
            <input
              type="text"
              name="reasonName"
              id="reasonName"
              value={reason.reasonName}
              placeholder="Ecrire un motif"
              autoComplete="motif"
              className="ml-5 w-80 rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
            />
          </div>
          <button
            type="submit"
            onClick={handleSubmit}
            className="rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
          >
            Enregistrer
          </button>
        </form>
      </>
    );
  }
  function  handleSubmit(event) {
    event.preventDefault();
  
    console.log(reasons);
    const token = localStorage.getItem("token");
    const config = {
      headers: { Authorization: `Bearer ${token}` },
    };
    axios.post("http://127.0.0.1:8000/api/reasons", reasons, config)
      .then((response) => {
        console.log(response);
        window.location.reload();
      })
  }
}
